CREATE TABLE IF NOT EXISTS news (N_ID int(11) NOT NULL, DESCRIPTION text, NEWS_DATE datetime DEFAULT NULL, STORY_HEAD text, YANDEX_URL text, PRIMARY KEY (N_ID));
CREATE TABLE IF NOT EXISTS comment (ID int(11) NOT NULL, AGENCY_NAME varchar(255) DEFAULT NULL, COMMENT text, RATING int(11) DEFAULT NULL, URL text, FK_NEWS_ID int(11) DEFAULT NULL, PRIMARY KEY (ID), KEY FK_COMMENT_FK_NEWS_ID (FK_NEWS_ID), CONSTRAINT FK_COMMENT_FK_NEWS_ID FOREIGN KEY (FK_NEWS_ID) REFERENCES news (N_ID));